        nav_anfahrt: "Yol Tarifi",
        hero_title: "Berlin Schöneberg'de Diş Hekiminiz",
        team_title: "Ekip",
        team_dentist: "Diş Hekimi",
        team_university: "(Ege Üniversitesi İzmir)",
