// Translation utility functions
class TranslationManager {
    constructor() {
        this.currentLanguage = 'de'; // Default to German
        this.loadTranslations();
    }

    loadTranslations() {
        // This will be called when the page loads
        this.updatePageContent();
    }

    setLanguage(language) {
        if (translations[language]) {
            this.currentLanguage = language;
            this.updatePageContent();
            // Store language preference in localStorage
            localStorage.setItem('preferredLanguage', language);
        }
    }

    getTranslation(key) {
        return translations[this.currentLanguage][key] || translations['de'][key] || key;
    }

    updatePageContent() {
        // Update all elements with data-translate attribute
        const elements = document.querySelectorAll('[data-translate]');
        elements.forEach(element => {
            const key = element.getAttribute('data-translate');
            const translation = this.getTranslation(key);

            if (element.tagName === 'INPUT' && element.type === 'text') {
                element.placeholder = translation;
            } else if (element.getAttribute('data-translate') === 'eigenlabor_content') {
                element.innerHTML = translation;
            } else {
                element.textContent = translation;
            }
        });
    }

    // Initialize language from localStorage or browser language
    initializeLanguage() {
        const savedLanguage = localStorage.getItem('preferredLanguage');
        const browserLanguage = navigator.language.split('-')[0];

        if (savedLanguage && translations[savedLanguage]) {
            this.setLanguage(savedLanguage);
        } else if (translations[browserLanguage]) {
            this.setLanguage(browserLanguage);
        } else {
            this.setLanguage('de'); // Default to German
        }
    }
}

// Create global translation manager instance
window.translationManager = new TranslationManager();

// Initialize when DOM is loaded
document.addEventListener('DOMContentLoaded', function () {
    window.translationManager.initializeLanguage();
});

